// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

//openzeppelin imports
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Nonces.sol";
import "./IWETH.sol";

interface IPaysec {
    function getPurchaseClone(address) external view returns(bool);
}

contract PSC is ERC20, Ownable, ERC20Permit, ERC20Votes{
    address constant WETH9 = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address public vendor;
    address public rewardsPool;
    address internal devAddress;
    uint public constant MAX_SUPPLY = 100000000 * 10 ** 18; //100 million total supply

    //receive function
    receive() external payable {
    }

    function nonces(address _owner) public view virtual override(ERC20Permit, Nonces) returns (uint256) {
        return super.nonces(_owner);
    }

    mapping(address => User) public users;
    address public paysecAddress;
    //uint public dividendPerToken;

    struct User {
        //address userAddress;
        uint dividendPerTokenWithdrawn;
        uint withdrawalCredit;
    }

    constructor() ERC20("PaySec", "PSC") ERC20Permit("PaySec") Ownable(msg.sender){
        vendor = msg.sender;
        devAddress = msg.sender;
        //mint 50 million tokens to owner (initial supply) - 50 million for inflation
        _mint(msg.sender, 50000000 * 10 ** decimals());
        //100 mil total supply
    }

    //consider making this a call once functions
    function changePaysec(address _contractAddress) external {
        require(msg.sender == owner());
        paysecAddress = _contractAddress;
    }

    event Withdraw(address indexed user, uint amount);    

//==========================================TOKEN CONTROL ============================================================
    //allow vendors to mint/sell tokens for ETH e.g. Crowdsale contract
    function setVendor(address _vendor) public {
        require(msg.sender == owner() || msg.sender == vendor, "Only owner or vendor can set vendor");
        vendor = _vendor;
    }

    function setRewardsPool(address _rewardsPool) public {
        require(msg.sender == owner(), "Only owner or vendor can set rewards pool");
        rewardsPool = _rewardsPool;
    }

    //pause token transfers but allow token mints
    /*bool internal paused = false;
    function togglePauseTransactions() public {
        require(msg.sender == owner() || msg.sender == vendor, "Only owner and vendor can toggle pause transactions");
        paused = !paused;
    }*/

    //convert entitled withdrawal to credit before transferring.
    function _update(address from, address to, uint256 amount) internal override(ERC20, ERC20Votes) {
        /*if(paused){
            require(from == address(0) || from == owner() || from == vendor || from == devAddress || to == rewardsPool || from == rewardsPool, "Only mints or transfers from owner or vendor or devAddress or rewardsPool are allowed when paused");
        }*/
        super._update(from, to, amount);
        if(totalSupply() > MAX_SUPPLY){
            revert("Total supply exceeded");
        }
    }

    function mint(address account, uint amount) external {
        require(msg.sender == owner() || IPaysec(paysecAddress).getPurchaseClone(msg.sender) || msg.sender == vendor, "Only implementations or owners or vendors are allowed to mint new tokens");
        _mint(account, amount);
    }
}

contracts/VestedFunder.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
pragma abicoder v2;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import './INonfungiblePositionManager.sol';
//import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "./IWETH.sol";

interface IPSC is IERC20{
    function togglePauseTransactions() external;
    function setVendor(address newOwner) external;
    function decimals() external view returns(uint);
    function owner() external view returns(address);
    function mint(address to, uint256 amount) external returns (uint256);
}

interface RewardsPool {
    function newStake(uint amount, uint stakeTime) external;
}

contract PSCICO is Ownable{
    int24 internal constant MAX_TICK = 887272;
    int24 internal constant MIN_TICK = -MAX_TICK;
    address internal PSC;
    address immutable nonfungiblePositionManagerAddress = 0xC36442b4a4522E871399CD717aBDD847Ab11FE88;
    INonfungiblePositionManager public immutable nonfungiblePositionManager = INonfungiblePositionManager(nonfungiblePositionManagerAddress);
    address immutable WETH9 = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address internal devWallet;
    uint public deploymentTime;
    uint public timeLimit = 30 days;
    uint public poolPSC;
    uint public goalAmount;
    uint public liquidityPSC;
    uint public liquidityETH;
    uint internal referalCommision = 50; //5%
    uint internal ratioDivisor = 1000;
    uint internal devCut = 200;
    uint public poolETH;
    uint public weightedETHRaised;

    //initial ETH for pseudo pool, 10 ether equates to 50 ETH goal
    bool isPool = false;

    mapping(address => Contributor) public contributors; //amount are in PSC
    uint public soldAmount;

    //ethReceived is the amount of ETH received by the referer (5% of the total ETH contributed by the buyer)
    //contribution is the full amount of ETH contributed by the buyer
    //amount is the amount of PSC tokens the buyer gets (95% of the total PSC tokens bought by the buyer)
    //amount of referer goes up to 5% of the total PSC tokens bought by the buyer
    struct Contributor{
        bool isReferer;
        uint ethReceived;
        uint contribution;
        uint weightedContribution;
        uint pscWithdrawn;
    }

    constructor(address _PSC) Ownable(msg.sender) {
        PSC = _PSC;
        deploymentTime = block.timestamp;
        devWallet = msg.sender;
        contributors[msg.sender] = Contributor(true, 0, 0, 1, 0);
    }

    receive() external payable {}

    //ADMIN FUNCTIONS
    //limit token updates to owner and transfer ownership to launcher before this
    bool internal isLaunched = false;
    function launchICO() external {
        require(msg.sender == owner(), "Only owner can launch ICO");
        require(!isLaunched, "ICO already launched");
        isLaunched = true;
        IPSC psc = IPSC(PSC);
        uint pscDecimals = 10 ** psc.decimals();
        //30M PSC for sale, 20M goal
        poolPSC = 30000000 * pscDecimals;
        require(psc.transferFrom(msg.sender, address(this), poolPSC), "Transfer to ICO failed");
        emit ICOLaunched(msg.sender, poolPSC, block.timestamp);
    }

    //add a referer to help market ICO
    function addReferer(address referer) external {
        require(msg.sender == devWallet, "Only dev can add referer");
        require(referer != address(0), "Referer cannot be 0 address");
        require(contributors[referer].isReferer == false, "Referer already added");
        contributors[referer].isReferer = true;
        emit RefererAdded(referer, msg.sender);
    }

    //collect all pool fees
    function collectAllFees(uint256 tokenId) external returns (uint256 amount0, uint256 amount1) {
        require(msg.sender == owner(), "Only owner can collect fees");
        // set amount0Max and amount1Max to uint256.max to collect all fees
        INonfungiblePositionManager.CollectParams memory params =
            INonfungiblePositionManager.CollectParams({
                tokenId: tokenId,
                recipient: msg.sender,
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            });

        (amount0, amount1) = nonfungiblePositionManager.collect(params);

        // send collected feed back to owner
        emit FeesCollected(msg.sender, tokenId, amount0, amount1);
    }

    //USER FUNCTIONS
    function buyPSC(address referer) external payable {
        require(isLaunched, "ICO not launched");
        require(msg.value > 0, "Value must be greater than 0");
        require(!isEnded(), "ICO is ended");
        if(referer != address(0)) {
            require(contributors[referer].isReferer == true, "Referer not found");
        } else{
            referer = devWallet;
        }
        //buyer gets an amount of PSC tokens equal to 95% of the pool conversion rate
        uint multiplier = getMultiplier();
        contributors[msg.sender].contribution += msg.value;
        uint weightedContribution = msg.value * multiplier / ratioDivisor;
        contributors[msg.sender].weightedContribution += weightedContribution;
        //referer also gets an amount of ETH equal to 5% of the pool conversion rate
        contributors[referer].ethReceived += msg.value * referalCommision / ratioDivisor;
        //update variables
        poolETH += msg.value;
        weightedETHRaised += msg.value * multiplier / ratioDivisor;
        emit PSCBought(msg.sender, referer, msg.value, contributors[msg.sender].weightedContribution);
    }

    function isEnded() public view returns(bool){
        return block.timestamp > deploymentTime + timeLimit;
    }

    //anyone can call this function
    bool internal endICOCalled = false;
    function endICO() public {
        require(isLaunched, "ICO not launched");
        require(isEnded(), "ICO is still active");
        require(!endICOCalled, "ICO already ended");
        endICOCalled = true;
        IPSC psc = IPSC(PSC);
        // add coins into liquidity pool
        createLiquidityPool();
        mintLiquidityPosition();
        //transfer vendor role to owner (timelock)
        psc.setVendor(psc.owner());
        //transfer devCut to dev wallet
        uint devAmount = poolETH * devCut / ratioDivisor;
        payable(devWallet).transfer(devAmount);
        emit ICOEnded(msg.sender, block.timestamp);
    }

    address internal pool;
    function createLiquidityPool() internal returns(address){
        //create  and initial liquidity pool
        uint24 fee = 3000;
        address token0 = PSC;
        address token1 = WETH9;
        if(WETH9 < PSC){
            token0 = WETH9;
            token1 = PSC;
        }
        //create liquidity pool
        //ratio of liquidity 
        //liquidity_psc / liquidity_eth = poolPSC / weightedETHRaised
        //liquidity_eth = liquidity_psc * weightedETHRaised / poolPSC
        liquidityETH = address(this).balance * (ratioDivisor - devCut - referalCommision) / ratioDivisor;
        liquidityPSC = poolPSC * liquidityETH / weightedETHRaised;
        IPSC psc = IPSC(PSC);
        psc.mint(address(this), liquidityPSC);
        uint160 sqrtPriceX96;

        //half of funds added to liquidity pool
        if(WETH9 < PSC){
            //95% of ETH
            sqrtPriceX96 = calculateSqrtPriceX96(liquidityETH, liquidityPSC);
        } else{
            sqrtPriceX96 = calculateSqrtPriceX96(liquidityPSC, liquidityETH);
        }
        pool = nonfungiblePositionManager.createAndInitializePoolIfNecessary(token0, token1, fee, sqrtPriceX96);
        emit LiquidityPoolCreated(pool, block.timestamp);
        return pool;
    }

    function mintLiquidityPosition() internal returns(uint256 tokenId, uint128 liquidity, uint256 amount0, uint256 amount1){
        IERC20 psc = IERC20(PSC);
        IWETH weth = IWETH(WETH9);
        address token0 = PSC;
        address token1 = WETH9;
        if(WETH9 < PSC){
            token0 = WETH9;
            token1 = PSC;
        }
        uint24 fee = 3000;
        //tokens to deposit (95% of ETH)
        uint amount0Desired = liquidityPSC;
        uint amount1Desired = liquidityETH;
        //deposit into weth
        weth.deposit{value: amount1Desired}();
        // Approve the position manager
        psc.approve(address(nonfungiblePositionManager), amount0Desired);
        weth.approve(address(nonfungiblePositionManager), amount1Desired);
        //add liquidity to the pool
        if(WETH9 < PSC){
            amount0Desired = amount1Desired;
            amount1Desired = liquidityPSC;
        }
        INonfungiblePositionManager.MintParams memory params =
            INonfungiblePositionManager.MintParams({
                token0: token0,
                token1: token1,
                fee: fee,
                tickLower: MIN_TICK -  MIN_TICK % int24(fee / 50),
                tickUpper: MAX_TICK - MAX_TICK % int24(fee / 50),
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: 0,
                amount1Min: 0,
                recipient: address(this),
                deadline: block.timestamp
            });

        (tokenId, liquidity, amount0, amount1) = nonfungiblePositionManager.mint(params);

        // Remove allowance and refund in both assets.
        if(token1 == WETH9){
            if (amount1 < amount1Desired) {
                weth.approve(nonfungiblePositionManagerAddress, 0); 
                weth.withdraw(amount1Desired - amount1);
            }
            if (amount0 < amount0Desired) {
                psc.approve(nonfungiblePositionManagerAddress, 0);
            }
        } else {
            if (amount0 < amount0Desired) {
                weth.approve(nonfungiblePositionManagerAddress, 0); 
                weth.withdraw(amount0Desired - amount0);
            }
            if (amount1 < amount1Desired) {
                psc.approve(nonfungiblePositionManagerAddress, 0); 
            }
        }

        emit LiquidityPositionMinted(tokenId, liquidity, amount0, amount1, block.timestamp);
    }

    function getMultiplier() public view returns(uint){
        //calculate multiplier based on closeness to end of ICO
        uint timePassed = block.timestamp - deploymentTime;
        uint multiplier = 2000 - 1000 * (timePassed) / timeLimit;
        if(multiplier < 1000) multiplier = 1000;
        return multiplier;
    }

    function getPool() public view returns(address){
        return pool;
    }

    //PSC claims can be made right after purchase
    function claimPSC() external {
        require(contributors[msg.sender].contribution > 0, "No PSC to claim");
        require(endICOCalled, "ICO is still active");
        uint multiplier = contributors[msg.sender].weightedContribution * ratioDivisor / contributors[msg.sender].contribution;
        uint cliffPeriod = timeLimit * (multiplier) / ratioDivisor;
        require(block.timestamp - deploymentTime > cliffPeriod, "Cliff period not over"); //Cliff period of 1 - multiplier times timelimit
        IERC20 psc = IERC20(PSC);
        uint amount = poolPSC * contributors[msg.sender].weightedContribution / weightedETHRaised;
        require(contributors[msg.sender].pscWithdrawn < amount, "Already claimed all PSC");
        uint elapsedVestingTime = block.timestamp - (deploymentTime + cliffPeriod);
        uint vestingTime = timeLimit * multiplier / ratioDivisor;
        if(elapsedVestingTime > vestingTime) elapsedVestingTime = vestingTime;
        uint unlockedAmount = amount * elapsedVestingTime / vestingTime;
        uint pscWithdrawn = contributors[msg.sender].pscWithdrawn;
        uint amountToWithdraw = unlockedAmount - pscWithdrawn;
        contributors[msg.sender].pscWithdrawn = unlockedAmount;
        psc.transfer(msg.sender, amountToWithdraw);
        emit PSCWithdrawn(msg.sender, amountToWithdraw, block.timestamp);
    }

    //ETH claims can only be made after the ICO ends
    function claimETH() external {
        require(contributors[msg.sender].ethReceived > 0, "No ETH to claim");
        require(endICOCalled, "ICO is still active");
        uint ethAmount = contributors[msg.sender].ethReceived;
        contributors[msg.sender].ethReceived = 0;
        payable(msg.sender).transfer(ethAmount);
        emit ETHWithdrawn(msg.sender, ethAmount, block.timestamp);
    }

    //helper functions

    function calculateSqrtPriceX96(uint256 amount0, uint256 amount1) public pure returns(uint160){
        return uint160(sqrt((10 ** 18) * amount1 / amount0) * 2 ** 96 / (10 ** 9));
    }

    function sqrt(uint x) internal pure returns (uint y) {
        uint z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }
    //==============================================EVENTS=============================================
    event ICOLaunched(address indexed owner, uint poolPSC, uint timestamp);
    event RefererAdded(address indexed referer, address indexed addedBy);
    event FeesCollected(address indexed owner, uint256 tokenId, uint256 amount0, uint256 amount1);
    event PSCBought(address indexed buyer, address indexed referer, uint ethAmount, uint weightedContribution);
    event ICOEnded(address indexed endedBy, uint timestamp);
    event LiquidityPoolCreated(address pool, uint timestamp);
    event LiquidityPositionMinted(uint256 tokenId, uint128 liquidity, uint256 amount0, uint256 amount1, uint timestamp);
    event PSCWithdrawn(address indexed user, uint amountWithdrawn, uint timestamp);
    event ETHWithdrawn(address indexed user, uint amount, uint timestamp);
}

